mapper.select:
SELECT count(*) FROM free_board WHERE bo_del_yn='N'
AND bo_title LIKE '%' || #{search.searchWord} ||'%' AND bo_writer LIKE '%' || #{search.searchWord} ||'%' AND bo_content LIKE '%' || #{search.searchWord} ||'%'
AND bo_category= #{search.searchCategory}

SELECT * FROM ( SELECT a.*, rownum AS rnum FROM ( SELECT bo_no , bo_title , bo_category , bo_writer , bo_pass , bo_content , bo_hit ,
to_char(bo_reg_date, 'YYYY-MM-DD') AS bo_reg_date , to_char(bo_mod_date, 'YYYY-MM-DD') AS bo_mod_date ,
b.comm_nm AS bo_category_nm , bo_del_yn FROM free_board a, comm_code b WHERE bo_del_yn='N' AND a.bo_category=b.comm_cd
AND bo_title LIKE '%' || #{search.searchWord} ||'%' AND bo_writer LIKE '%' || #{search.searchWord} ||'%' AND bo_content LIKE '%' || #{search.searchWord} ||'%'
AND bo_category= #{search.searchCategory}
ORDER BY bo_no desc ) a ) b WHERE rnum between #{paging.firstRow} and #{paging.lastRow}

mapper.insert:
UPDATE free_board SET
    bo_del_yn='Y'
WHERE bo_no= #{boNo}
