mapper.update:
UPDATE member
SET
    mem_name = #{memName}
  , mem_zip = #{memZip}
  , mem_add1 = #{memAdd1}
  , mem_add2 = #{memAdd2}
  , mem_bir = #{memBir}
  , mem_mail = #{memMail}
  , mem_hp = #{memHp}
  , mem_job = #{memJob}
  , mem_hobby = #{memHobby}
WHERE mem_id = #{memId}

UPDATE member
SET mem_del_yn = 'Y'
WHERE mem_id = #{memId}
